const Pos = require( '../position.js' );
const allMatches = require( '../util/allMatches.js' );

module.exports = function ( text, start, errors, regex, set, message ) {
	allMatches( regex, text, start, ( match, start, end ) => {
		if ( !set[ match[1] ] ) {
			errors.push({
				start: start,
				end: end,
				message: `'${ match[0] }' ${ message }`
			});
		}
	});
};
